import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Orders = () => {
    const [loading, setLoading] = useState(false); // State to manage loading status
    const [orders, setOrders] = useState([]); // State to store orders
    const [error, setError] = useState(null); // State to store errors

    const fetchOrders = async () => {
        try {
            setLoading(true); // Set loading state to true
            const token = localStorage.getItem('token'); // Retrieve the token from localStorage
            const response = await axios.get('http://localhost:5002/api/orders/my-orders', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
            });
            setOrders(response.data); // Update orders state with fetched data
        } catch (err) {
            setError('Failed to fetch orders'); // Set error state if request fails
            console.error('Error fetching orders:', err);
        } finally {
            setLoading(false); // Set loading state back to false
        }
    };

    useEffect(() => {
        fetchOrders(); // Fetch orders when component mounts
    }, []);

    if (loading) return <div>Loading...</div>; // Show loading state
    if (error) return <div>Error: {error}</div>; // Show error state

    return (
        <div>
            <h1>My Orders</h1>
            <ul>
                {orders.map(order => (
                    <li key={order._id}>
                        <h2>Order ID: {order._id}</h2>
                        <p>Status: {order.status}</p>
                        <p>Total Amount: ${order.totalAmount}</p>
                        <ul>
                            {order.items.map(item => (
                                <li key={item.menuItemId._id}>
                                    {item.menuItemId.dishName} - Quantity: {item.quantity}
                                </li>
                            ))}
                        </ul>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Orders;
