import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaShoppingBag, FaTrash, FaMoneyBill } from 'react-icons/fa';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './OrderManagement.css';

const OrderManagement = ({ onBack }) => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5002/api/orders', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOrders(response.data);
    } catch (error) {
      toast.error(error.response?.data?.message || 'Error fetching orders');
    } finally {
      setLoading(false);
    }
  };

  const handleStatusChange = async (orderId, newStatus) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put(
        `http://localhost:5002/api/orders/${orderId}/status`,
        { status: newStatus },
        { headers: { Authorization: `Bearer ${token}` }}
      );

      setOrders(orders.map(order => 
        order._id === orderId ? response.data : order
      ));

      toast.success(`Order status updated to ${newStatus}`);
    } catch (error) {
      toast.error(error.response?.data?.message || 'Error updating order status');
    }
  };

  const handlePaymentStatusChange = async (orderId, newStatus) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put(
        `http://localhost:5002/api/orders/${orderId}/payment`,
        { paymentStatus: newStatus },
        { headers: { Authorization: `Bearer ${token}` }}
      );

      setOrders(orders.map(order => 
        order._id === orderId ? response.data : order
      ));

      toast.success(`Payment status updated to ${newStatus}`);
    } catch (error) {
      toast.error(error.response?.data?.message || 'Error updating payment status');
    }
  };

  const handleDeleteOrder = async (orderId) => {
    if (!window.confirm('Are you sure you want to delete this order?')) return;

    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5002/api/orders/${orderId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setOrders(orders.filter(order => order._id !== orderId));
      toast.success('Order deleted successfully');
    } catch (error) {
      toast.error(error.response?.data?.message || 'Error deleting order');
    }
  };

  const getStatusColor = (status) => {
    const colors = {
      pending: '#ffa500',
      confirmed: '#007bff',
      preparing: '#800080',
      ready: '#008000',
      delivered: '#006400',
      cancelled: '#ff0000'
    };
    return colors[status] || '#000000';
  };

  const getPaymentStatusColor = (status) => {
    const colors = {
      pending: '#ffa500',
      completed: '#008000',
      failed: '#ff0000'
    };
    return colors[status] || '#000000';
  };

  const filteredOrders = orders.filter(order => {
    const matchesSearch = 
      order.userId?.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      order._id.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesFilter = filterStatus === 'all' || order.status === filterStatus;

    return matchesSearch && matchesFilter;
  });

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="order-management">
      <div className="order-management-header">
        <div className="header-left">
          <button className="back-button" onClick={onBack}>
            ← Back to Dashboard
          </button>
          <h2>Order Management</h2>
        </div>
        <div className="header-right">
          <div className="search-bar">
            <input
              type="text"
              placeholder="Search by customer name or order ID..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          <select
            value={filterStatus}
            onChange={(e) => setFilterStatus(e.target.value)}
            className="status-filter"
          >
            <option value="all">All Status</option>
            <option value="pending">Pending</option>
            <option value="confirmed">Confirmed</option>
            <option value="preparing">Preparing</option>
            <option value="ready">Ready</option>
            <option value="delivered">Delivered</option>
            <option value="cancelled">Cancelled</option>
          </select>
        </div>
      </div>

      <div className="orders-grid">
        {filteredOrders.map(order => (
          <div key={order._id} className="order-card">
            <div className="order-header">
              <div className="order-icon">
                <FaShoppingBag />
              </div>
              <div className="order-id">Order #{order._id.slice(-6)}</div>
              <div className="order-date">
                {new Date(order.createdAt).toLocaleString()}
              </div>
            </div>

            <div className="order-customer">
              <h4>Customer Details</h4>
              <p>Name: {order.userId?.name}</p>
              <p>Phone: {order.userId?.phone}</p>
              <p>Address: {order.deliveryAddress}</p>
            </div>

            <div className="order-items">
              <h4>Order Items</h4>
              {order.items.map((item, index) => (
                <div key={index} className="order-item">
                  <span>{item.menuItem?.name}</span>
                  <span>x{item.quantity}</span>
                  <span>₹{(item.menuItem?.price * item.quantity).toFixed(2)}</span> {/* Format individual item totals */}
                </div>
              ))}
              <div className="order-total">
                Total Amount: ₹{order.totalAmount.toFixed(2)} {/* Format total amount */}
              </div>
            </div>

            <div className="order-status-section">
              <div className="status-group">
                <label>Order Status:</label>
                <select
                  value={order.status}
                  onChange={(e) => handleStatusChange(order._id, e.target.value)}
                  style={{ color: getStatusColor(order.status) }}
                >
                  <option value="pending">Pending</option>
                  <option value="confirmed">Confirmed</option>
                  <option value="preparing">Preparing</option>
                  <option value="ready">Ready</option>
                  <option value="delivered">Delivered</option>
                  <option value="cancelled">Cancelled</option>
                </select>
              </div>

              <div className="status-group">
                <label>Payment Status:</label>
                <select
                  value={order.paymentStatus}
                  onChange={(e) => handlePaymentStatusChange(order._id, e.target.value)}
                  style={{ color: getPaymentStatusColor(order.paymentStatus) }}
                >
                  <option value="pending">Pending</option>
                  <option value="completed">Completed</option>
                  <option value="failed">Failed</option>
                </select>
              </div>
            </div>

            {order.specialInstructions && (
              <div className="special-instructions">
                <h4>Special Instructions:</h4>
                <p>{order.specialInstructions}</p>
              </div>
            )}

            <div className="order-actions">
              <button
                className="delete-button"
                onClick={() => handleDeleteOrder(order._id)}
                title="Delete Order"
              >
                <FaTrash />
              </button>
            </div>
          </div>
        ))}
      </div>

      {filteredOrders.length === 0 && (
        <div className="no-orders">
          {searchTerm || filterStatus !== 'all'
            ? 'No orders found matching your search criteria.'
            : 'No orders available.'}
        </div>
      )}

      <ToastContainer position="bottom-right" />
    </div>
  );
};

export default OrderManagement;
