import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import './OrderManagement.css';

const OrderManagement = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchOrders();
    // Set up polling for order updates
    const interval = setInterval(fetchOrders, 30000); // Poll every 30 seconds
    return () => clearInterval(interval);
  }, []);

  const fetchOrders = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        toast.error('Authentication required');
        return;
      }

      const response = await axios.get('http://localhost:5002/api/orders', {
        headers: { 
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      // Sort orders by date (newest first) and filter out deleted orders
      const sortedOrders = response.data
        .filter(order => !order.isDeleted)
        .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));

      setOrders(sortedOrders);
      setLoading(false);
    } catch (error) {
      const errorMessage = error.response?.data?.message || 'Failed to fetch orders';
      toast.error(errorMessage);
      setLoading(false);
    }
  };

  const handleStatusUpdate = async (orderId, newStatus) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        toast.error('Authentication required');
        return;
      }

      // First, validate the status transition
      const order = orders.find(o => o._id === orderId);
      if (!order) {
        toast.error('Order not found');
        return;
      }

      // Validate status transitions
      const validTransitions = {
        'Pending': ['Preparing', 'Cancelled'],
        'Preparing': ['Ready', 'Cancelled'],
        'Ready': ['Delivered', 'Cancelled'],
        'Delivered': [],
        'Cancelled': []
      };

      if (!validTransitions[order.status]?.includes(newStatus)) {
        toast.error(`Cannot change status from ${order.status} to ${newStatus}`);
        return;
      }

      const response = await axios.put(
        `http://localhost:5002/api/orders/${orderId}/status`,
        { status: newStatus },
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      if (response.data) {
        // Update the local state with the new order status
        setOrders(orders.map(order => 
          order._id === orderId 
            ? { ...order, status: newStatus }
            : order
        ));

        // Show success message
        toast.success(`Order status updated to ${newStatus}`);

        // Refresh orders to get latest data
        fetchOrders();
      }
    } catch (error) {
      const errorMessage = error.response?.data?.message || 'Failed to update order status';
      toast.error(errorMessage);

      // Refresh orders to ensure consistency
      fetchOrders();
    }
  };

  const formatDate = (dateString) => {
    const options = { 
      year: 'numeric', 
      month: 'short', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    };
    return new Date(dateString).toLocaleDateString('en-US', options);
  };

  const getStatusBadgeClass = (status) => {
    const statusClasses = {
      'Pending': 'status-pending',
      'Preparing': 'status-preparing',
      'Ready': 'status-ready',
      'Delivered': 'status-delivered',
      'Cancelled': 'status-cancelled'
    };
    return statusClasses[status] || '';
  };

  if (loading) {
    return <div className="loading">Loading orders...</div>;
  }

  return (
    <div className="order-management">
      <h2>Order Management</h2>
      <div className="table-container">
        <table className="orders-table">
          <thead>
            <tr>
              <th>Order ID</th>
              <th>Customer</th>
              <th>Items</th>
              <th>Total Amount</th>
              <th>Date</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(order => (
              <tr key={order._id} className={getStatusBadgeClass(order.status)}>
                <td className="order-id">{order._id.slice(-6).toUpperCase()}</td>
                <td className="customer-info">
                  <div>{order.userId?.name || 'Unknown'}</div>
                  <div className="email">{order.userId?.email || 'No email'}</div>
                </td>
                <td className="order-items">
                  {order.items.map(item => (
                    <div key={item._id} className="order-item">
                      {item.menuItemId?.dishName || 'Unknown Item'} × {item.quantity}
                    </div>
                  ))}
                </td>
                <td className="amount">₹{order.totalAmount?.toFixed(2) || '0.00'}</td>
                <td className="date">{formatDate(order.createdAt)}</td>
                <td>
                  <span className={`status-badge ${getStatusBadgeClass(order.status)}`}>
                    {order.status}
                  </span>
                </td>
                <td className="actions">
                  {order.status !== 'Delivered' && order.status !== 'Cancelled' && (
                    <div className="action-buttons">
                      <select 
                        className={`status-select ${getStatusBadgeClass(order.status)}`}
                        value={order.status}
                        onChange={(e) => handleStatusUpdate(order._id, e.target.value)}
                      >
                        <option value="Pending">Pending</option>
                        <option value="Preparing">Preparing</option>
                        <option value="Ready">Ready</option>
                        <option value="Delivered">Delivered</option>
                        <option value="Cancelled">Cancelled</option>
                      </select>
                    </div>
                  )}
                  {(order.status === 'Delivered' || order.status === 'Cancelled') && (
                    <span className={`status-badge ${getStatusBadgeClass(order.status)}`}>
                      {order.status}
                    </span>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default OrderManagement;
