import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaArrowLeft, FaDownload, FaChartLine, FaUsers, FaUtensils, FaMoneyBillWave } from 'react-icons/fa';
import './ReportsManagement.css';

const ReportsManagement = ({ onBack }) => {
  const [insights, setInsights] = useState({
    overview: {
      totalOrders: 0,
      totalRevenue: 0,
      averageOrderValue: 0,
      totalCustomers: 0
    },
    menuInsights: {
      topDishes: [],
      mealTypeDistribution: { Lunch: 0, Dinner: 0 },
      foodTypeDistribution: { Veg: 0, 'Non-veg': 0 }
    },
    orderInsights: {
      recentOrders: [],
      orderStatusDistribution: {},
      orderTrends: []
    },
    customerInsights: {
      newCustomers: 0,
      repeatCustomers: 0,
      topCustomers: []
    }
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [dateRange, setDateRange] = useState('week');

  useEffect(() => {
    fetchInsights();
  }, [dateRange]);

  const fetchInsights = async () => {
    try {
      setLoading(true);
      setError(null);

      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No authentication token found');
      }

      const response = await axios.get(
        `http://localhost:5002/api/reports?range=${dateRange}`,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      if (!response || !response.data) {
        throw new Error('Invalid response from server');
      }

      const data = response.data;

      // Set insights with proper null checks and defaults
      setInsights({
        overview: {
          totalOrders: data.totalOrders || 0,
          totalRevenue: data.totalRevenue || 0,
          averageOrderValue: data.totalOrders ? (data.totalRevenue / data.totalOrders).toFixed(2) : 0,
          totalCustomers: data.totalOrders || 0 // Using total orders as a proxy for now
        },
        menuInsights: {
          topDishes: Array.isArray(data.popularItems) ? data.popularItems.map(item => ({
            name: item.name || 'Unknown Item',
            orderCount: item.orderCount || 0
          })) : [],
          mealTypeDistribution: { Lunch: 0, Dinner: 0 }, // Default values for now
          foodTypeDistribution: { Veg: 0, 'Non-veg': 0 } // Default values for now
        },
        orderInsights: {
          recentOrders: Array.isArray(data.recentOrders) ? data.recentOrders.map(order => ({
            orderId: order.orderId || 'N/A',
            total: order.total || 0,
            date: new Date(order.date).toLocaleDateString(),
            status: order.status || 'pending'
          })) : [],
          orderStatusDistribution: {}, // Default value for now
          orderTrends: [] // Default value for now
        },
        customerInsights: {
          newCustomers: 0, // Default value for now
          repeatCustomers: 0, // Default value for now
          topCustomers: [] // Default value for now
        }
      });
    } catch (err) {
      console.error('Error fetching insights:', err);
      let errorMessage = 'An error occurred while fetching insights';

      if (err.response) {
        // Server responded with error
        errorMessage = err.response.data?.message || 'Server error occurred';
      } else if (err.request) {
        // No response received
        errorMessage = 'No response from server. Please check your connection.';
      } else {
        // Error in request setup
        errorMessage = err.message;
      }

      setError(errorMessage);

      // Set default values for insights on error
      setInsights({
        overview: { totalOrders: 0, totalRevenue: 0, averageOrderValue: 0, totalCustomers: 0 },
        menuInsights: {
          topDishes: [],
          mealTypeDistribution: { Lunch: 0, Dinner: 0 },
          foodTypeDistribution: { Veg: 0, 'Non-veg': 0 }
        },
        orderInsights: {
          recentOrders: [],
          orderStatusDistribution: {},
          orderTrends: []
        },
        customerInsights: {
          newCustomers: 0,
          repeatCustomers: 0,
          topCustomers: []
        }
      });
    } finally {
      setLoading(false);
    }
  };

  const handleExport = async () => {
    try {
      setError(null); // Clear any previous errors
      const token = localStorage.getItem('token');

      if (!token) {
        throw new Error('No authentication token found');
      }

      const response = await axios({
        method: 'GET',
        url: `http://localhost:5002/api/reports?range=${dateRange}&export=true`,
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        responseType: 'blob'
      });

      // Check if we have a valid response
      if (!response.data) {
        throw new Error('No data received for export');
      }

      // Create and trigger download
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `business-insights-${dateRange}.pdf`);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);
    } catch (err) {
      console.error('Error exporting report:', err);
      if (err.response) {
        setError(err.response.data?.message || 'Error exporting report');
      } else if (err.request) {
        setError('No response from server. Please check your connection.');
      } else {
        setError(err.message || 'Error exporting report');
      }
    }
  };

  if (loading) return <div className="loading">Loading insights...</div>;

  return (
    <div className="reports-container">
      <div className="reports-header">
        <button className="back-button" onClick={onBack}>
          <FaArrowLeft /> Back to Dashboard
        </button>
        <div className="header-controls">
          <select 
            value={dateRange} 
            onChange={(e) => setDateRange(e.target.value)}
            className="date-range-select"
          >
            <option value="week">Last Week</option>
            <option value="month">Last Month</option>
            <option value="year">Last Year</option>
          </select>
          <button className="export-button" onClick={handleExport}>
            <FaDownload /> Export Report
          </button>
        </div>
      </div>

      {error && <div className="error-message">{error}</div>}

      <div className="insights-grid">
        {/* Overview Section */}
        <div className="insight-section">
          <h2><FaChartLine /> Business Overview</h2>
          <div className="metrics-grid">
            <div className="metric-card">
              <h3>Total Orders</h3>
              <p>{insights.overview.totalOrders}</p>
            </div>
            <div className="metric-card">
              <h3>Total Revenue</h3>
              <p>₹{insights.overview.totalRevenue.toFixed(2)}</p>
            </div>
            <div className="metric-card">
              <h3>Average Order Value</h3>
              <p>₹{insights.overview.averageOrderValue}</p>
            </div>
            <div className="metric-card">
              <h3>Total Customers</h3>
              <p>{insights.overview.totalCustomers}</p>
            </div>
          </div>
        </div>

        {/* Menu Insights */}
        <div className="insight-section">
          <h2><FaUtensils /> Menu Performance</h2>
          <div className="menu-insights">
            <div className="top-dishes">
              <h3>Top Performing Dishes</h3>
              {insights.menuInsights.topDishes.map((dish, index) => (
                <div key={index} className="dish-item">
                  <span>{dish.name}</span>
                  <span>{dish.orderCount} orders</span>
                </div>
              ))}
            </div>
            <div className="distribution-charts">
              <div className="chart">
                <h3>Meal Type Distribution</h3>
                {Object.entries(insights.menuInsights.mealTypeDistribution).map(([type, count]) => (
                  <div key={type} className="distribution-bar">
                    <span>{type}</span>
                    <div className="bar" style={{ width: `${(count / insights.overview.totalOrders) * 100}%` }}></div>
                    <span>{count}</span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* Customer Insights */}
        <div className="insight-section">
          <h2><FaUsers /> Customer Analysis</h2>
          <div className="customer-insights">
            <div className="customer-metrics">
              <div className="metric">
                <h3>New Customers</h3>
                <p>{insights.customerInsights.newCustomers}</p>
              </div>
              <div className="metric">
                <h3>Repeat Customers</h3>
                <p>{insights.customerInsights.repeatCustomers}</p>
              </div>
            </div>
            <div className="top-customers">
              <h3>Top Customers</h3>
              {insights.customerInsights.topCustomers.map((customer, index) => (
                <div key={index} className="customer-item">
                  <span>{customer.name}</span>
                  <span>₹{customer.totalSpent.toFixed(2)}</span>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Order Trends */}
        <div className="insight-section">
          <h2><FaMoneyBillWave /> Order Analysis</h2>
          <div className="order-insights">
            <div className="order-status">
              <h3>Order Status Distribution</h3>
              {Object.entries(insights.orderInsights.orderStatusDistribution).map(([status, count]) => (
                <div key={status} className="status-bar">
                  <span>{status}</span>
                  <div className="bar" style={{ width: `${(count / insights.overview.totalOrders) * 100}%` }}></div>
                  <span>{count}</span>
                </div>
              ))}
            </div>
            <div className="recent-orders">
              <h3>Recent Orders</h3>
              {insights.orderInsights.recentOrders.map((order, index) => (
                <div key={index} className="order-item">
                  <span>#{order.orderId}</span>
                  <span>₹{order.total.toFixed(2)}</span>
                  <span>{order.date}</span>
                  <span>{order.status}</span>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ReportsManagement;
