import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './ReportsManagement.css';

const ReportsManagement = () => {
  const [totalOrders, setTotalOrders] = useState(0);
  const [totalRevenue, setTotalRevenue] = useState(0);
  const [orderCounts, setOrderCounts] = useState({});
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const onBack = () => {
    window.history.back();
  };

  const fetchReports = async (date) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No authentication token found');
      }

      const response = await axios.get(
        'http://localhost:5002/api/reports',
        {
          params: { date },
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      if (response.data) {
        setTotalOrders(response.data.totalOrders || 0);
        setTotalRevenue(response.data.totalRevenue || 0);
        setOrderCounts(response.data.orderStatusCounts || {});
      }
    } catch (err) {
      console.error('Error fetching reports:', err);
      setError('Error fetching reports data');
    }
  };

  useEffect(() => {
    fetchReports(selectedDate);
  }, [selectedDate]);

  const handleDateChange = (event) => {
    setSelectedDate(event.target.value);
  };

  const getStatusColor = (status) => {
    const colors = {
      'pending': '#ffc107',
      'processing': '#17a2b8',
      'completed': '#28a745',
      'cancelled': '#dc3545'
    };
    return colors[status.toLowerCase()] || '#6c757d';
  };

  return (
    <div>
      <button className="back-button" onClick={onBack}>
        ← Back to Dashboard
      </button>
      <div className="reports-container">
        <h2>Reports Management</h2>
        <div className="date-filter">
          <input
            type="date"
            value={selectedDate}
            onChange={handleDateChange}
            className="date-input"
          />
        </div>
        <div className="reports-content">
          <div className="metrics-grid">
            <div className="metric-card">
              <h3>Total Orders</h3>
              <p>{totalOrders}</p>
              <span className="date-label">on {new Date(selectedDate).toLocaleDateString()}</span>
              <div className="order-status-grid">
                {Object.entries(orderCounts).map(([status, count]) => (
                  <div key={status} className="status-count">
                    <span className="status-dot" style={{ backgroundColor: getStatusColor(status) }}></span>
                    <span className="status-label">{status}</span>
                    <span className="status-number">{count}</span>
                  </div>
                ))}
              </div>
            </div>
            <div className="metric-card">
              <h3>Total Revenue</h3>
              <p>₹{totalRevenue.toLocaleString('en-IN', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</p>
              <span className="date-label">on {new Date(selectedDate).toLocaleDateString()}</span>
            </div>
          </div>
          {error && <p className="error-message">{error}</p>}
        </div>
      </div>
    </div>
  );
};

export default ReportsManagement;
