import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaUser } from 'react-icons/fa';
import './Navbar.css';

const Navbar = () => {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showPopover, setShowPopover] = useState(false);
  const [user, setUser] = useState(null);
  const popoverRef = useRef(null);

  const checkAuthStatus = useCallback(() => {
    const userData = localStorage.getItem('user');
    const token = localStorage.getItem('token');

    if (userData && token) {
      setIsLoggedIn(true);
      setUser(JSON.parse(userData));
    } else {
      setIsLoggedIn(false);
      setUser(null);
    }
  }, []);

  useEffect(() => {
    // Check auth status on mount
    checkAuthStatus();

    // Listen for login/logout events
    window.addEventListener('userLogin', checkAuthStatus);
    window.addEventListener('storage', checkAuthStatus);

    return () => {
      window.removeEventListener('userLogin', checkAuthStatus);
      window.removeEventListener('storage', checkAuthStatus);
    };
  }, [checkAuthStatus]);

  useEffect(() => {
    // Close popover when clicking outside
    const handleClickOutside = (event) => {
      if (popoverRef.current && !popoverRef.current.contains(event.target)) {
        setShowPopover(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setIsLoggedIn(false);
    setUser(null);
    setShowPopover(false);
    // Dispatch logout event
    window.dispatchEvent(new Event('userLogin'));
    navigate('/');
  };

  const handleViewProfile = () => {
    setShowPopover(false);
    navigate('/profile');
  };

  return (
    <nav className="navbar">
      <div className="navbar-brand">
        <Link to="/">Tiffin Service</Link>
      </div>
      <div className="navbar-links">
        <Link to="/">Home</Link>
        {isLoggedIn && user && (
          <Link 
            to={user.userType === 'admin' ? '/admin/dashboard' : '/member/dashboard'} 
            className="dashboard-link"
          >
            Dashboard
          </Link>
        )}
        {isLoggedIn && user ? (
          <div className="profile-section" ref={popoverRef}>
            <button 
              className="profile-button"
              onClick={() => setShowPopover(!showPopover)}
            >
              <FaUser className="profile-icon" />
              <span className="profile-name">{user.name || 'User'}</span>
            </button>
            {showPopover && (
              <div className="profile-popover">
                <button onClick={handleViewProfile}>View Profile</button>
                <button onClick={handleLogout}>Logout</button>
              </div>
            )}
          </div>
        ) : (
          <>
            <Link to="/login">Login</Link>
            <Link to="/signup">Sign Up</Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
