import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaArrowLeft, FaDownload } from 'react-icons/fa';
import './Dashboard.css';

const ReportsManagement = ({ onBack }) => {
  const [reports, setReports] = useState({
    totalOrders: 0,
    totalRevenue: 0,
    popularItems: [],
    recentOrders: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [dateRange, setDateRange] = useState('week'); // week, month, year

  // Fetch reports whenever dateRange changes
  useEffect(() => {
    fetchReports();
  }, [dateRange]);

  const fetchReports = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      if (!token) {
        setError('No authentication token found. Please login again.');
        return;
      }

      const response = await axios.get(`http://localhost:5002/api/reports?range=${dateRange}`, { // Fixed API endpoint
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      setReports(response.data); // Update reports with fetched data
      setError(null);
    } catch (err) {
      if (err.response) {
        setError(`Error: ${err.response.data.error || 'Failed to fetch reports'}`);
      } else if (err.request) {
        setError('No response from server. Please try again.');
      } else {
        setError('Error setting up request. Please try again.');
      }
      console.error('Error fetching reports:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleExportReport = async () => {
    try {
      const token = localStorage.getItem('token');

      if (!token) {
        setError('No authentication token found. Please login again.');
        return;
      }

      const response = await axios.get(
        `http://localhost:5002/api/reports/export?range=${dateRange}`, // Fixed API endpoint
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          responseType: 'blob'
        }
      );

      // Create a download link for the report
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `report-${dateRange}-${new Date().toISOString().split('T')[0]}.csv`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      if (err.response) {
        setError(`Error: ${err.response.data.error || 'Failed to export report'}`);
      } else if (err.request) {
        setError('No response from server. Please try again.');
      } else {
        setError('Error setting up request. Please try again.');
      }
      console.error('Error exporting report:', err);
    }
  };

  if (loading) {
    return (
      <div className="dashboard-container">
        <div className="loading">Loading reports...</div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <button className="back-button" onClick={onBack}>
          <FaArrowLeft /> Back to Dashboard
        </button>
        <h2>Business Reports</h2>
      </div>

      {error && <div className="error-message">{error}</div>}

      <div className="reports-controls">
        <select
          value={dateRange}
          onChange={(e) => setDateRange(e.target.value)}
          className="date-range-select"
        >
          <option value="week">Last Week</option>
          <option value="month">Last Month</option>
          <option value="year">Last Year</option>
        </select>

        <button className="export-button" onClick={handleExportReport}>
          <FaDownload /> Export Report
        </button>
      </div>

      <div className="reports-grid">
        <div className="report-card">
          <h3>Total Orders</h3>
          <div className="report-value">{reports.totalOrders}</div>
        </div>

        <div className="report-card">
          <h3>Total Revenue</h3>
          {/* Use .toFixed(2) to format the totalRevenue */}
          <div className="report-value">₹{reports.totalRevenue.toFixed(2)}</div>
        </div>

        <div className="report-card full-width">
          <h3>Popular Items</h3>
          <div className="popular-items">
            {reports.popularItems.map((item, index) => (
              <div key={index} className="popular-item">
                <span>{item.name}</span>
                <span>{item.orderCount} orders</span>
              </div>
            ))}
          </div>
        </div>

        <div className="report-card full-width">
          <h3>Recent Orders</h3>
          <div className="recent-orders">
            {reports.recentOrders.map((order, index) => (
              <div key={index} className="recent-order">
                <span>Order #{order.orderId}</span>
                {/* Use .toFixed(2) to format the total for each recent order */}
                <span>₹{order.total.toFixed(2)}</span>
                <span>{new Date(order.date).toLocaleDateString()}</span>
              </div>
            ))}
          </div>
        </div>
      </div>

    </div>
  );
};

export default ReportsManagement;
