import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Orders.css';

const Orders = () => {
    const [loading, setLoading] = useState(false);
    const [orders, setOrders] = useState([]);
    const [error, setError] = useState(null);

    const fetchOrders = async () => {
        setLoading(true);
        setError(null);

        try {
            // Test the base API endpoint
            await axios.get('http://localhost:5002/api/test');

            // Test the orders router
            await axios.get('http://localhost:5002/api/orders/test');

            const token = localStorage.getItem('token');
            if (!token) throw new Error('User is not authenticated.');

            const response = await axios.get('http://localhost:5002/api/orders/my-orders', {
                headers: {
                    Authorization: `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
            });

            setOrders(response.data);
        } catch (err) {
            setError(err.response?.data?.message || 'Failed to fetch orders.');
            console.error('Error fetching orders:', err);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchOrders();
    }, []);

    if (loading) return (
        <div className="orders-loading">
            <div className="loader"></div>
            <p>Loading your orders...</p>
        </div>
    );

    if (error) return (
        <div className="orders-error">
            <div className="error-icon">❌</div>
            <p>{error}</p>
        </div>
    );

    return (
        <div className="orders-container">
            <div className="orders-header">
                <h1>My Orders</h1>
                <div className="orders-count">{orders.length} {orders.length === 1 ? 'Order' : 'Orders'}</div>
            </div>

            <div className="orders-list">
                {orders.map(order => (
                    <div key={order._id} className="order-card">
                        <div className="order-card-header">
                            <div className="order-info">
                                <div className="order-number">
                                    <span className="label">Order #</span>
                                    <span className="value">{order._id.slice(-8)}</span>
                                </div>
                                <div className={`order-status status-${order.status.toLowerCase()}`}>
                                    {order.status}
                                </div>
                            </div>
                            <div className="order-date">
                                {new Date(order.createdAt).toLocaleDateString('en-US', {
                                    year: 'numeric',
                                    month: 'short',
                                    day: 'numeric'
                                })}
                            </div>
                        </div>

                        <div className="order-items-list">
                            {order.items.map(item => (
                                <div key={item.menuItemId._id} className="order-item">
                                    <div className="item-details">
                                        <span className="item-name">{item.menuItemId.dishName}</span>
                                        <div className="item-meta">
                                            <span className="item-quantity">Qty: {item.quantity}</span>
                                            <span className="item-price">₹{item.menuItemId.price * item.quantity}</span>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>

                        <div className="order-card-footer">
                            <div className="order-total">
                                <span className="total-label">Total Amount</span>
                                <span className="total-value">₹{order.totalAmount}</span>
                            </div>
                            <button className="track-order-btn">
                                Track Order
                                <span className="btn-icon">→</span>
                            </button>
                        </div>
                    </div>
                ))}
            </div>

            {orders.length === 0 && (
                <div className="no-orders">
                    <div className="empty-icon">🛍️</div>
                    <h3>No Orders Yet</h3>
                    <p>When you place an order, it will appear here</p>
                </div>
            )}
        </div>
    );
};

export default Orders;
