import React, { useEffect, useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaPlus, FaMinus, FaShoppingCart, FaTrash } from 'react-icons/fa';
import './Menu.css';

const Menu = () => {
  const navigate = useNavigate();
  const [menuItems, setMenuItems] = useState([]);
  const [cart, setCart] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [cartVisible, setCartVisible] = useState(false);
  const [orderSuccess, setOrderSuccess] = useState(false);

  // Load cart from localStorage on component mount
  useEffect(() => {
    const savedCart = localStorage.getItem('cart');
    if (savedCart) {
      try {
        setCart(JSON.parse(savedCart));
      } catch (err) {
        console.error('Error loading cart:', err);
      }
    }
  }, []);

  // Save cart to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cart));
  }, [cart]);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    fetchMenu();
  }, [navigate]);

  const fetchMenu = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      console.log('Fetching menu items...');
      const response = await axios.get('http://localhost:5002/api/menu', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      const items = response.data;
      console.log('Fetched Menu Items:', items);

      // Validate menu items have required fields
      const validItems = items.map(item => {
        if (!item.mealType) console.warn(`Menu item ${item._id} missing mealType`);
        if (!item.foodType) console.warn(`Menu item ${item._id} missing foodType`);
        if (!item.portionSize) console.warn(`Menu item ${item._id} missing portionSize`);
        return item;
      });

      setMenuItems(validItems);
      setError(null);
    } catch (error) {
      console.error('Error fetching menu:', error);
      setError('Failed to fetch menu items');
    } finally {
      setLoading(false);
    }
  };

  const calculateTotal = useCallback(() => {
    return Object.entries(cart).reduce((total, [itemId, quantity]) => {
      const item = menuItems.find(i => i._id === itemId);
      return item ? total + (item.price * quantity) : total;
    }, 0);
  }, [cart, menuItems]);

  const handleQuantityChange = (itemId, change) => {
    setCart(prevCart => {
      const currentQty = prevCart[itemId] || 0;
      const newQty = Math.max(0, currentQty + change);

      if (newQty === 0) {
        const { [itemId]: removed, ...rest } = prevCart;
        return rest;
      }

      return {
        ...prevCart,
        [itemId]: newQty
      };
    });
  };

  const clearCart = () => {
    setCart({});
    localStorage.removeItem('cart');
  };

  const handlePlaceOrder = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      // First, get the menu item details
      const firstItemId = Object.keys(cart)[0];
      console.log('First menu item ID:', firstItemId);
      console.log('All menu items:', menuItems);

      const firstMenuItem = menuItems.find(item => item._id === firstItemId);
      console.log('Selected menu item:', firstMenuItem);

      if (!firstMenuItem) {
        console.error('Menu item not found:', firstItemId);
        setError('Error: Could not find menu item details');
        return;
      }

      // Prepare order items array
      const orderItems = Object.entries(cart).map(([menuItemId, quantity]) => ({
        menuItemId,
        quantity
      }));

      if (orderItems.length === 0) {
        setError('Please add items to your cart');
        return;
      }

      // Ensure proper capitalization of type values
      const capitalizeFirstLetter = (str) => {
        if (!str) return '';
        return str.charAt(0).toUpperCase() + str.slice(1).toLowerCase();
      };

      // Create order data with properly capitalized type values
      const orderData = {
        items: orderItems,
        totalAmount: calculateTotal(),
        mealType: capitalizeFirstLetter(firstMenuItem.mealType),
        foodType: firstMenuItem.foodType === 'non-veg' ? 'Non-veg' : capitalizeFirstLetter(firstMenuItem.foodType),
        portionSize: capitalizeFirstLetter(firstMenuItem.portionSize)
      };

      console.log('Creating order with data:', orderData);
      console.log('Type values being sent:', {
        mealType: orderData.mealType,
        foodType: orderData.foodType,
        portionSize: orderData.portionSize
      });

      const response = await axios.post('http://localhost:5002/api/orders', 
        orderData,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      console.log('Order created successfully:', response.data);

      setOrderSuccess(true);
      clearCart();
      setTimeout(() => {
        setOrderSuccess(false);
        navigate('/member/dashboard');
      }, 2000);
    } catch (err) {
      console.error('Error creating order:', err.response?.data);
      setError('Failed to place order: ' + (err.response?.data?.message || err.message));
    } finally {
      setLoading(false);
    }
  };

  const getItemQuantity = (itemId) => cart[itemId] || 0;

  const getTotalItems = () => {
    return Object.values(cart).reduce((sum, quantity) => sum + quantity, 0);
  };

  if (loading && !menuItems.length) {
    return <div className="menu-container"><p>Loading menu...</p></div>;
  }

  if (error) {
    return (
      <div className="menu-container">
        <div className="error-message">
          <p>{error}</p>
          <button onClick={fetchMenu}>Try Again</button>
        </div>
      </div>
    );
  }

  return (
    <div className="menu-container">
      <div className="menu-header">
        <h1>Today's Menu</h1>
        <div className="header-actions">
          <button 
            className="cart-toggle-btn"
            onClick={() => setCartVisible(!cartVisible)}
          >
            <FaShoppingCart />
            {getTotalItems() > 0 && (
              <span className="cart-badge">{getTotalItems()}</span>
            )}
          </button>
          <button className="back-btn" onClick={() => navigate('/member/dashboard')}>
            Back to Dashboard
          </button>
        </div>
      </div>

      {orderSuccess && (
        <div className="success-message">
          Order placed successfully! Redirecting to dashboard...
        </div>
      )}

      <div className="menu-content">
        <div className="menu-list">
          {menuItems.map(item => (
            <div key={item._id} className="menu-card">
              <div className="menu-card-header">
                <h3>{item.dishName}</h3>
                <span className={`food-type-badge ${item.foodType.toLowerCase()}`}>
                  {item.foodType}
                </span>
              </div>
              <div className="menu-card-content">
                <div className="menu-details">
                  <div className="detail-item">
                    <span className="detail-label">Meal Type:</span>
                    <span className={`detail-value meal-type ${item.mealType.toLowerCase()}`}>
                      {item.mealType}
                    </span>
                  </div>
                  <div className="detail-item">
                    <span className="detail-label">Portion:</span>
                    <span className={`detail-value portion-size ${item.portionSize.toLowerCase()}`}>
                      {item.portionSize}
                    </span>
                  </div>
                </div>
                <div className="price-section">
                  <span className="price-label">Price:</span>
                  <span className="price-value">₹{item.price}</span>
                </div>
              </div>
              <div className="quantity-controls">
                <button 
                  onClick={() => handleQuantityChange(item._id, -1)}
                  disabled={!cart[item._id]}
                  className={`quantity-btn ${!cart[item._id] ? 'disabled' : ''}`}
                  aria-label="Decrease quantity"
                >
                  <FaMinus />
                </button>
                <span className="quantity-display">{getItemQuantity(item._id)}</span>
                <button 
                  onClick={() => handleQuantityChange(item._id, 1)}
                  className="quantity-btn"
                  aria-label="Increase quantity"
                >
                  <FaPlus />
                </button>
              </div>
              {cart[item._id] > 0 && (
                <div className="item-total">
                  Total: ₹{(item.price * cart[item._id]).toFixed(2)}
                </div>
              )}
            </div>
          ))}
        </div>

        <div className={`order-summary ${cartVisible ? 'visible' : ''}`}>
          <div className="order-summary-header">
            <h3>Order Summary</h3>
            {Object.keys(cart).length > 0 && (
              <button className="clear-cart-btn" onClick={clearCart}>
                <FaTrash /> Clear Cart
              </button>
            )}
          </div>

          {Object.keys(cart).length === 0 ? (
            <p className="empty-cart-message">Your cart is empty</p>
          ) : (
            <>
              <div className="cart-items">
                {Object.entries(cart).map(([itemId, quantity]) => {
                  const item = menuItems.find(i => i._id === itemId);
                  if (!item) return null;
                  return (
                    <div key={itemId} className="cart-item">
                      <div className="cart-item-details">
                        <span className="item-name">{item.dishName}</span>
                        <span className="item-quantity">x {quantity}</span>
                      </div>
                      <span className="item-total">₹{item.price * quantity}</span>
                    </div>
                  );
                })}
              </div>
              <div className="cart-summary">
                <div className="subtotal">
                  <span>Subtotal</span>
                  <span>₹{calculateTotal()}</span>
                </div>
                <div className="total">
                  <strong>Total</strong>
                  <strong>₹{calculateTotal()}</strong>
                </div>
                <button 
                  className="place-order-btn"
                  onClick={handlePlaceOrder}
                  disabled={loading || Object.keys(cart).length === 0}
                >
                  {loading ? 'Placing Order...' : 'Place Order'}
                </button>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Menu;
