import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaPlus, FaMinus } from 'react-icons/fa';
import './Menu.css';

const Menu = () => {
  const navigate = useNavigate();
  const [menuItems, setMenuItems] = useState([]);
  const [cart, setCart] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    fetchMenu();
  }, [navigate]);

  const fetchMenu = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5002/api/menu', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      setMenuItems(response.data);
      setError(null);
    } catch (err) {
      setError('Failed to fetch menu');
      console.error('Error fetching menu:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleQuantityChange = (itemId, change) => {
    setCart(prevCart => {
      const currentQty = prevCart[itemId] || 0;
      const newQty = Math.max(0, currentQty + change);

      if (newQty === 0) {
        const { [itemId]: removed, ...rest } = prevCart;
        return rest;
      }

      return {
        ...prevCart,
        [itemId]: newQty
      };
    });
  };

  const handlePlaceOrder = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      const orderItems = Object.entries(cart).map(([menuItemId, quantity]) => ({
        menuItemId,
        quantity
      }));

      if (orderItems.length === 0) {
        setError('Please add items to your cart');
        return;
      }

      await axios.post('http://localhost:5002/api/orders', 
        { items: orderItems },
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      setCart({});
      setError(null);
      alert('Order placed successfully!');
      navigate('/member/dashboard');
    } catch (err) {
      setError('Failed to place order');
      console.error('Error placing order:', err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="menu-container"><p>Loading menu...</p></div>;
  }

  if (error) {
    return <div className="menu-container"><p className="error-text">{error}</p></div>;
  }

  return (
    <div className="menu-container">
      <div className="menu-header">
        <h1>Today's Menu</h1>
        <button className="back-btn" onClick={() => navigate('/member/dashboard')}>
          Back to Dashboard
        </button>
      </div>

      <div className="menu-content">
        <div className="menu-list">
          {menuItems.map(item => (
            <div key={item._id} className="menu-card">
              <div className="menu-card-content">
                <h3>{item.dishName}</h3>
                <p className="description">{item.description}</p>
                <p className="price">₹{item.price}</p>
              </div>
              <div className="quantity-controls">
                <button 
                  onClick={() => handleQuantityChange(item._id, -1)}
                  disabled={!cart[item._id]}
                >
                  <FaMinus />
                </button>
                <span>{cart[item._id] || 0}</span>
                <button 
                  onClick={() => handleQuantityChange(item._id, 1)}
                >
                  <FaPlus />
                </button>
              </div>
            </div>
          ))}
        </div>

        {Object.keys(cart).length > 0 && (
          <div className="order-summary">
            <h3>Order Summary</h3>
            <div className="cart-items">
              {Object.entries(cart).map(([itemId, quantity]) => {
                const item = menuItems.find(i => i._id === itemId);
                return (
                  <div key={itemId} className="cart-item">
                    <span>{item.dishName} x {quantity}</span>
                    <span>₹{item.price * quantity}</span>
                  </div>
                );
              })}
            </div>
            <div className="total">
              <strong>Total:</strong>
              <strong>₹{
                Object.entries(cart).reduce((total, [itemId, quantity]) => {
                  const item = menuItems.find(i => i._id === itemId);
                  return total + (item.price * quantity);
                }, 0)
              }</strong>
            </div>
            <button 
              className="place-order-btn"
              onClick={handlePlaceOrder}
              disabled={loading}
            >
              {loading ? 'Placing Order...' : 'Place Order'}
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Menu;
