import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaShoppingBag, FaTrash, FaMoneyBill } from 'react-icons/fa';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './OrderManagement.css';
import { useNavigate } from 'react-router-dom';

const OrderManagement = ({ onBack }) => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      const response = await axios.get('http://localhost:5002/api/orders', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      setOrders(response.data);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching orders:', err);
      setError('Failed to fetch orders');
      setLoading(false);
    }
  };

  const handleStatusChange = async (orderId, newStatus) => {
    try {
      const token = localStorage.getItem('token');
      await axios.patch(`http://localhost:5002/api/orders/${orderId}/status`, 
        { status: newStatus },
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      // Refresh orders after status update
      fetchOrders();
    } catch (err) {
      console.error('Error updating order status:', err);
      setError('Failed to update order status');
    }
  };

  const handlePaymentStatusChange = async (orderId, newStatus) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put(
        `http://localhost:5002/api/orders/${orderId}/payment`,
        { paymentStatus: newStatus },
        { headers: { Authorization: `Bearer ${token}` }}
      );

      setOrders(orders.map(order => 
        order._id === orderId ? response.data : order
      ));

      toast.success(`Payment status updated to ${newStatus}`);
    } catch (error) {
      toast.error(error.response?.data?.message || 'Error updating payment status');
    }
  };

  const handleDeleteOrder = async (orderId) => {
    if (!window.confirm('Are you sure you want to delete this order?')) return;

    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5002/api/orders/${orderId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setOrders(orders.filter(order => order._id !== orderId));
      toast.success('Order deleted successfully');
    } catch (error) {
      toast.error(error.response?.data?.message || 'Error deleting order');
    }
  };

  if (loading) return <div>Loading orders...</div>;
  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="order-management">
      <div className="order-management-header">
        <div className="header-left">
          <button className="back-button" onClick={onBack}>
            ← Back to Dashboard
          </button>
          <h2>Order Management</h2>
        </div>
      </div>
      <div className="orders-table-container">
        <table className="orders-table">
          <thead>
            <tr>
              <th>Order ID</th>
              <th>Customer</th>
              <th>Items</th>
              <th>Meal Type</th>
              <th>Food Type</th>
              <th>Portion Size</th>
              <th>Total Amount</th>
              <th>Status</th>
              <th>Payment Status</th>
              <th>Special Instructions</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(order => (
              <tr key={order._id} className={`status-${order.status.toLowerCase()}`}>
                <td>{order._id}</td>
                <td>{order.userId?.name || 'Unknown'}</td>
                <td>
                  <ul className="items-list">
                    {order.items.map((item, index) => (
                      <li key={index}>
                        {item.menuItemId?.dishName || 'Unknown Item'} x {item.quantity}
                      </li>
                    ))}
                  </ul>
                </td>
                <td>{order.mealType}</td>
                <td>{order.foodType}</td>
                <td>{order.portionSize}</td>
                <td>₹{order.totalAmount}</td>
                <td>{order.status}</td>
                <td>{order.paymentStatus}</td>
                <td>{order.specialInstructions || '-'}</td>
                <td>
                  <select
                    value={order.status}
                    onChange={(e) => handleStatusChange(order._id, e.target.value)}
                    className="status-select"
                  >
                    <option value="pending">Pending</option>
                    <option value="confirmed">Confirmed</option>
                    <option value="preparing">Preparing</option>
                    <option value="ready">Ready</option>
                    <option value="delivered">Delivered</option>
                    <option value="cancelled">Cancelled</option>
                  </select>
                  <button
                    onClick={() => handlePaymentStatusChange(order._id, 'completed')}
                    className={`payment-button ${order.paymentStatus === 'completed' ? 'paid' : ''}`}
                  >
                    <FaMoneyBill />
                    {order.paymentStatus === 'completed' ? 'Paid' : 'Mark Paid'}
                  </button>
                  <button
                    onClick={() => handleDeleteOrder(order._id)}
                    className="delete-button"
                  >
                    <FaTrash />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <ToastContainer position="bottom-right" />
    </div>
  );
};

export default OrderManagement;
