import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaArrowLeft, FaDownload } from 'react-icons/fa';
import './Dashboard.css';

const ReportsManagement = ({ onBack }) => {
  const [reports, setReports] = useState({
    totalOrders: 0,
    totalRevenue: 0,
    popularItems: [],
    recentOrders: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [dateRange, setDateRange] = useState('week');

  useEffect(() => {
    fetchReports();
  }, [dateRange]);

  const fetchReports = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      if (!token) {
        setError('No authentication token found. Please login again.');
        return;
      }

      const response = await axios.get(`http://localhost:5002/api/reports?range=${dateRange}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      setReports(response.data);
      setError(null);
    } catch (err) {
      console.error('Error fetching reports:', err);
      setError(err.response?.data?.message || 'Failed to fetch reports.');
    } finally {
      setLoading(false);
    }
  };

  const handleExportReport = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('No authentication token found. Please login again.');
        return;
      }

      const response = await axios.get(
        `http://localhost:5002/api/reports/export?range=${dateRange}`,
        {
          headers: { Authorization: `Bearer ${token}` },
          responseType: 'blob'
        }
      );

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `report-${dateRange}-${new Date().toISOString().split('T')[0]}.csv`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error('Error exporting report:', err);
      setError('Failed to export report.');
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <button onClick={onBack}>
        <FaArrowLeft /> Back
      </button>
      <h1>Reports</h1>
      {error && <p>{error}</p>}
      <select value={dateRange} onChange={(e) => setDateRange(e.target.value)}>
        <option value="week">Last Week</option>
        <option value="month">Last Month</option>
        <option value="year">Last Year</option>
      </select>
      <button onClick={handleExportReport}>
        <FaDownload /> Export
      </button>
      <div>
        <h2>Total Orders: {reports.totalOrders}</h2>
        <h2>Total Revenue: ₹{reports.totalRevenue.toFixed(2)}</h2>
      </div>
      <div>
        <h3>Popular Items:</h3>
        {reports.popularItems.map((item, idx) => (
          <p key={idx}>{item.name}: {item.orderCount} orders</p>
        ))}
      </div>
      <div>
        <h3>Recent Orders:</h3>
        {reports.recentOrders.map((order, idx) => (
          <p key={idx}>Order #{order.orderId}: ₹{order.total} on {new Date(order.date).toLocaleDateString()}</p>
        ))}
      </div>
    </div>
  );
};

export default ReportsManagement;
