import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaUtensils, FaUser, FaShoppingCart, FaPlus, FaMinus } from 'react-icons/fa';
import './Dashboard.css';

const MemberDashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [activeComponent, setActiveComponent] = useState(null);
  const [orders, setOrders] = useState([]);
  const [menuItems, setMenuItems] = useState([]);
  const [cart, setCart] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Get user data from localStorage
    const userData = localStorage.getItem('user');
    if (!userData) {
      navigate('/login');
      return;
    }

    const parsedUser = JSON.parse(userData);
    if (parsedUser.userType !== 'member') {
      navigate('/login');
      return;
    }

    setUser(parsedUser);
    if (activeComponent === 'orders') {
      fetchOrders();
    } else if (activeComponent === 'menu') {
      fetchMenu();
    }
  }, [navigate, activeComponent]);

  const fetchOrders = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5002/api/orders/my-orders', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      setOrders(response.data);
      setError(null);
    } catch (err) {
      setError('Failed to fetch orders');
      console.error('Error fetching orders:', err);
    } finally {
      setLoading(false);
    }
  };

  const fetchMenu = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5002/api/menu', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      setMenuItems(response.data);
      setError(null);
    } catch (err) {
      setError('Failed to fetch menu');
      console.error('Error fetching menu:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleQuantityChange = (itemId, change) => {
    setCart(prevCart => {
      const currentQty = prevCart[itemId] || 0;
      const newQty = Math.max(0, currentQty + change);

      if (newQty === 0) {
        const { [itemId]: removed, ...rest } = prevCart;
        return rest;
      }

      return {
        ...prevCart,
        [itemId]: newQty
      };
    });
  };

  const handlePlaceOrder = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      const orderItems = Object.entries(cart).map(([menuItemId, quantity]) => ({
        menuItemId,
        quantity
      }));

      if (orderItems.length === 0) {
        setError('Please add items to your cart');
        return;
      }

      await axios.post('http://localhost:5002/api/orders', 
        { items: orderItems },
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      setCart({});
      setError(null);
      alert('Order placed successfully!');
      setActiveComponent('orders');
    } catch (err) {
      setError('Failed to place order');
      console.error('Error placing order:', err);
    } finally {
      setLoading(false);
    }
  };

  const renderOrders = () => (
    <div className="orders-section">
      <h2>My Orders</h2>
      {loading ? (
        <p>Loading orders...</p>
      ) : error ? (
        <p className="error-text">{error}</p>
      ) : orders.length === 0 ? (
        <p>No orders found</p>
      ) : (
        <div className="orders-list">
          {orders.map(order => (
            <div key={order._id} className="order-card">
              <h3>Order #{order._id.slice(-6)}</h3>
              <p>Date: {new Date(order.createdAt).toLocaleDateString()}</p>
              <p>Status: {order.status}</p>
              <p>Total: ₹{order.totalAmount}</p>
              <button 
                className="view-details-btn"
                onClick={() => navigate(`/orders/${order._id}`)}
              >
                View Details
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );

  const renderMenu = () => (
    <div className="menu-section">
      <h2>Today's Menu</h2>
      {loading ? (
        <p>Loading menu...</p>
      ) : error ? (
        <p className="error-text">{error}</p>
      ) : menuItems.length === 0 ? (
        <p>No menu items available</p>
      ) : (
        <>
          <div className="menu-list">
            {menuItems.map(item => (
              <div key={item._id} className="menu-card">
                <div className="menu-card-content">
                  <h3>{item.dishName}</h3>
                  <p className="description">{item.description}</p>
                  <p className="price">₹{item.price}</p>
                </div>
                <div className="quantity-controls">
                  <button 
                    onClick={() => handleQuantityChange(item._id, -1)}
                    disabled={!cart[item._id]}
                  >
                    <FaMinus />
                  </button>
                  <span>{cart[item._id] || 0}</span>
                  <button 
                    onClick={() => handleQuantityChange(item._id, 1)}
                  >
                    <FaPlus />
                  </button>
                </div>
              </div>
            ))}
          </div>
          {Object.keys(cart).length > 0 && (
            <div className="order-summary">
              <h3>Order Summary</h3>
              <div className="cart-items">
                {Object.entries(cart).map(([itemId, quantity]) => {
                  const item = menuItems.find(i => i._id === itemId);
                  return (
                    <div key={itemId} className="cart-item">
                      <span>{item.dishName} x {quantity}</span>
                      <span>₹{item.price * quantity}</span>
                    </div>
                  );
                })}
              </div>
              <div className="total">
                <strong>Total:</strong>
                <strong>₹{
                  Object.entries(cart).reduce((total, [itemId, quantity]) => {
                    const item = menuItems.find(i => i._id === itemId);
                    return total + (item.price * quantity);
                  }, 0)
                }</strong>
              </div>
              <button 
                className="place-order-btn"
                onClick={handlePlaceOrder}
                disabled={loading}
              >
                {loading ? 'Placing Order...' : 'Place Order'}
              </button>
            </div>
          )}
        </>
      )}
    </div>
  );

  if (!user) return null;

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>Welcome, {user.name}!</h1>
      </div>

      <div className="dashboard-content">
        <div className="dashboard-card">
          <FaShoppingCart className="card-icon" />
          <h3>My Orders</h3>
          <p>View and track your tiffin orders</p>
          <button 
            className="dashboard-button"
            onClick={() => setActiveComponent('orders')}
          >
            View Orders
          </button>
        </div>

        <div className="dashboard-card">
          <FaUtensils className="card-icon" />
          <h3>Menu</h3>
          <p>Browse today's menu and place orders</p>
          <button 
            className="dashboard-button"
            onClick={() => setActiveComponent('menu')}
          >
            View Menu
          </button>
        </div>

        <div className="dashboard-card">
          <FaUser className="card-icon" />
          <h3>Profile</h3>
          <p>Update your profile information</p>
          <button 
            className="dashboard-button"
            onClick={() => navigate('/profile')}
          >
            Edit Profile
          </button>
        </div>
      </div>

      {activeComponent === 'orders' && renderOrders()}
      {activeComponent === 'menu' && renderMenu()}
    </div>
  );
};

export default MemberDashboard;
