import React, { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { FaUsers, FaUtensils, FaChartBar, FaCog } from 'react-icons/fa';
import UserManagement from './UserManagement';
import './Dashboard.css';

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [activeComponent, setActiveComponent] = useState(null);

  useEffect(() => {
    // Get user data from localStorage
    const userData = localStorage.getItem('user');
    if (!userData) {
      navigate('/login');
      return;
    }

    const parsedUser = JSON.parse(userData);
    if (parsedUser.userType !== 'admin') {
      navigate('/login');
      return;
    }

    setUser(parsedUser);
  }, [navigate]);

  if (!user) return null;

  const handleComponentChange = (component) => {
    setActiveComponent(component);
  };

  if (activeComponent === 'users') {
    return <UserManagement onBack={() => setActiveComponent(null)} />;
  }

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>Welcome, {user.name}!</h1>
        <p>Admin Dashboard</p>
      </div>

      <div className="dashboard-content">
        <div className="dashboard-grid">
          <div className="dashboard-card">
            <div className="card-icon">
              <FaChartBar />
            </div>
            <h3>Orders Management</h3>
            <p>View and manage all customer orders</p>
            <button className="dashboard-button">Manage Orders</button>
          </div>

          <div className="dashboard-card">
            <div className="card-icon">
              <FaUsers />
            </div>
            <h3>User Management</h3>
            <p>Manage customer accounts</p>
            <button 
              className="dashboard-button"
              onClick={() => handleComponentChange('users')}
            >
              Manage Users
            </button>
          </div>

          <div className="dashboard-card">
            <div className="card-icon">
              <FaUtensils />
            </div>
            <h3>Menu Management</h3>
            <p>Add and manage menu items</p>
            <Link to="/admin/menu" className="dashboard-button">Manage Menu</Link>
          </div>

          <div className="dashboard-card">
            <div className="card-icon">
              <FaCog />
            </div>
            <h3>Reports</h3>
            <p>View business analytics</p>
            <button className="dashboard-button">View Reports</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
